import React, { Component } from "react";
import LocationService from "../services/LocationService";

class ListLocationComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      locations: [],
    };

    this.addLocation = this.addLocation.bind(this);
  }

  componentDidMount() {
    LocationService.getLocation().then((res) => {
      this.setState({ locations: res.data });
    });
  }

  addLocation() {
    this.props.history.push("/add-location");
  }

  render() {
    return (
      <div>
        <h2 className="text-center">Location List</h2>
        <div className="row">
          <div className="row">
            <button className="btn btn-primary" onClick={this.addLocation}>
              Add Location
            </button>
          </div>
          <table className="table table-striped table-bordered">
            <thead>
              <tr>
                <th>Country</th>
                <th>Province</th>
                <th>District</th>
                <th>Sub District</th>
                <th>Group Location</th>
                <th>Number Home</th>
              </tr>
            </thead>

            <tbody>
              {this.state.locations.map((location) => (
                <tr key={location.id}>
                  <td>{location.country}</td>
                  <td>{location.province}</td>
                  <td>{location.district}</td>
                  <td>{location.subDistrict}</td>
                  <td>{location.group}</td>
                  <td>{location.numberHome}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default ListLocationComponent;
