import React, { Component } from 'react';

class CreateLocationComponent extends Component {

    constructor(props){
        super(props)
        
        this.state = {
            country: '',
            province: '',
            district: '',
            subDistrict: '',
            group: '',
            numberHome: ''
        }
    }

    changeFirstNameHandler= (event) => {
        this.setState({firstName: event.target.value});
    }

    changeLastNameHandler= (event) => {
        this.setState({lastName: event.target.value});
    }

    changeEmailHandler= (event) => {
        this.setState({emailId: event.target.value});
    }

    cancel(){
        this.props.history.push('/employees');
    }

    getTitle(){
        if(this.state.id === '_add'){
            return <h3 className="text-center">Add Location</h3>
        }else{
            return <h3 className="text-center">Update Location</h3>
        }
    }
    render() {
        return (
            <div>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                {
                                    this.getTitle()
                                }
                                <div className = "card-body">
                                    <form>
                                        <div className = "form-group">
                                            <label> Country: </label>
                                            <input placeholder="Country" name="country" className="form-control" 
                                                value={this.state.country} onChange={this.changeCountryHandler}/>
                                        </div>
                                        idLocation: '',
            country: '',
            province: '',
            district: '',
            subDistrict: '',
            group: '',
            numberHome: ''
                                        <div className = "form-group">
                                            <label> Province: </label>
                                            <input placeholder="Province" name="province" className="form-control" 
                                                value={this.state.province} onChange={this.changeProvinceHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> District: </label>
                                            <input placeholder="District" name="district" className="form-control" 
                                                value={this.state.district} onChange={this.changeDistrictHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Province: </label>
                                            <input placeholder="Province" name="province" className="form-control" 
                                                value={this.state.province} onChange={this.changeProvinceHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Province: </label>
                                            <input placeholder="Province" name="province" className="form-control" 
                                                value={this.state.province} onChange={this.changeProvinceHandler}/>
                                        </div>
                                        

                                        <button className="btn btn-success" onClick={this.saveOrUpdateEmployee}>Save</button>
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default CreateLocationComponent;
